// Enhanced Dashboard.js with modern design
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import toast from 'react-hot-toast';
import { 
  Plus, 
  FileText, 
  Edit, 
  Trash2, 
  Clock, 
  User,
  Tag,
  Bot,
  MessageCircle,
  Sparkles,
  TrendingUp,
  Users,
  Calendar
} from 'lucide-react';

const Dashboard = () => {
  const { user } = useAuth();
  const [documents, setDocuments] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTags, setSelectedTags] = useState([]);
  const [allTags, setAllTags] = useState([]);
  const [showQA, setShowQA] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [qaLoading, setQaLoading] = useState(false);
  const [aiLoading, setAiLoading] = useState({});

  useEffect(() => {
    fetchDocuments();
    fetchRecentActivity();
  }, []);

  useEffect(() => {
    // Extract all unique tags
    const tags = new Set();
    documents.forEach(doc => {
      doc.tags.forEach(tag => tags.add(tag));
    });
    setAllTags([...tags]);
  }, [documents]);

  const fetchDocuments = async () => {
    try {
      const response = await axios.get('/documents');
      setDocuments(response.data.documents);
    } catch (error) {
      toast.error('Failed to fetch documents');
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentActivity = async () => {
    try {
      const response = await axios.get('/documents/activity');
      setRecentActivity(response.data);
    } catch (error) {
      console.error('Failed to fetch recent activity');
    }
  };

  const handleDelete = async (docId) => {
    if (!window.confirm('Are you sure you want to delete this document?')) {
      return;
    }

    try {
      await axios.delete(`/documents/${docId}`);
      toast.success('Document deleted successfully');
      fetchDocuments();
      fetchRecentActivity();
    } catch (error) {
      toast.error('Failed to delete document');
    }
  };

  const handleTagFilter = (tag) => {
    if (selectedTags.includes(tag)) {
      setSelectedTags(selectedTags.filter(t => t !== tag));
    } else {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  const filteredDocuments = selectedTags.length > 0
    ? documents.filter(doc => 
        selectedTags.some(tag => doc.tags.includes(tag))
      )
    : documents;

  const handleAskQuestion = async () => {
    if (!question.trim()) return;

    setQaLoading(true);
    try {
      const response = await axios.post('/ai/question', { question });
      setAnswer(response.data.answer);
    } catch (error) {
      toast.error('Failed to get answer');
    } finally {
      setQaLoading(false);
    }
  };

  // Generate summary for existing document
  const handleGenerateSummary = async (docId) => {
    const doc = documents.find(d => d._id === docId);
    if (!doc) return;

    // Check permissions before attempting
    if (!canEdit(doc)) {
      toast.error('You don\'t have permission to edit this document');
      return;
    }

    setAiLoading(prev => ({ ...prev, [`summary-${docId}`]: true }));
    try {
      const response = await axios.post('/ai/summarize', { content: doc.content });
      
      // Use the new AI-specific route for updating summary
      await axios.put(`/ai/documents/${docId}/summary`, {
        summary: response.data.summary
      });
      
      toast.success('Summary generated and saved!');
      fetchDocuments();
    } catch (error) {
      if (error.response?.status === 403) {
        toast.error('You don\'t have permission to edit this document');
      } else {
        toast.error('Failed to generate summary');
      }
    } finally {
      setAiLoading(prev => ({ ...prev, [`summary-${docId}`]: false }));
    }
  };

  // Generate tags for existing document
  const handleGenerateTags = async (docId) => {
    const doc = documents.find(d => d._id === docId);
    if (!doc) return;

    // Check permissions before attempting
    if (!canEdit(doc)) {
      toast.error('You don\'t have permission to edit this document');
      return;
    }

    setAiLoading(prev => ({ ...prev, [`tags-${docId}`]: true }));
    try {
      const response = await axios.post('/ai/tags', { content: doc.content });
      
      // Use the new AI-specific route for updating tags
      await axios.put(`/ai/documents/${docId}/tags`, {
        tags: response.data.tags
      });
      
      toast.success('Tags generated and saved!');
      fetchDocuments();
    } catch (error) {
      if (error.response?.status === 403) {
        toast.error('You don\'t have permission to edit this document');
      } else {
        toast.error('Failed to generate tags');
      }
    } finally {
      setAiLoading(prev => ({ ...prev, [`tags-${docId}`]: false }));
    }
  };

  const canEdit = (doc) => {
    return user.role === 'admin' || doc.createdBy._id === user.id;
  };

  const userDocuments = documents.filter(doc => doc.createdBy._id === user.id);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Hero Section */}
        <div className="bg-gradient-to-r from-indigo-600 to-purple-600 rounded-xl shadow-lg text-white p-8 md:p-12 mb-12">
          <h1 className="text-4xl font-bold mb-2">Welcome Back, {user.name}!</h1>
          <p className="text-indigo-200 mb-8">Your knowledge hub at a glance. Let's make today productive.</p>
          
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6 text-center">
            <div className="bg-indigo-700/50 p-4 rounded-lg backdrop-blur-sm">
              <p className="text-3xl font-bold">{documents.length}</p>
              <p className="text-indigo-200 text-sm">Total Docs</p>
            </div>
            <div className="bg-indigo-700/50 p-4 rounded-lg backdrop-blur-sm">
              <p className="text-3xl font-bold">{userDocuments.length}</p>
              <p className="text-indigo-200 text-sm">Your Docs</p>
            </div>
            <div className="bg-indigo-700/50 p-4 rounded-lg backdrop-blur-sm">
              <p className="text-3xl font-bold">{allTags.length}</p>
              <p className="text-indigo-200 text-sm">Total Tags</p>
            </div>
            <div className="bg-indigo-700/50 p-4 rounded-lg backdrop-blur-sm">
              <p className="text-3xl font-bold">{recentActivity.length}</p>
              <p className="text-indigo-200 text-sm">Recent Updates</p>
            </div>
          </div>
          
          <div className="flex flex-col sm:flex-row gap-4 mt-8">
            <button
              onClick={() => setShowQA(!showQA)}
              className="inline-flex items-center px-6 py-3 border border-white/20 text-sm font-medium rounded-lg text-white bg-white/10 hover:bg-white/20 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-white/50 transition-all duration-200 backdrop-blur-sm"
            >
              <MessageCircle className="h-5 w-5 mr-2" />
              {showQA ? 'Hide' : 'Show'} Team Q&A
            </button>
            
            <Link
              to="/documents/new"
              className="inline-flex items-center px-6 py-3 border border-transparent text-sm font-medium rounded-lg text-indigo-600 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200 shadow-sm"
            >
              <Plus className="h-5 w-5 mr-2" />
              New Document
            </Link>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-3">
            {/* Q&A Section */}
            {showQA && (
              <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
                <h2 className="text-xl font-bold text-gray-800 mb-6 flex items-center">
                  <div className="bg-purple-100 p-2 rounded-lg mr-3">
                    <Bot className="h-5 w-5 text-purple-600" />
                  </div>
                  Ask AI About Your Documents
                </h2>
                
                <div className="space-y-4">
                  <div>
                    <textarea
                      value={question}
                      onChange={(e) => setQuestion(e.target.value)}
                      placeholder="Ask a question about your team's documents..."
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-all duration-200"
                      rows="3"
                    />
                  </div>
                  
                  <button
                    onClick={handleAskQuestion}
                    disabled={qaLoading || !question.trim()}
                    className="inline-flex items-center px-6 py-3 border border-transparent text-sm font-medium rounded-lg text-white bg-purple-600 hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 shadow-sm"
                  >
                    {qaLoading ? (
                      <>
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                        Thinking...
                      </>
                    ) : (
                      <>
                        <Sparkles className="h-4 w-4 mr-2" />
                        Ask AI
                      </>
                    )}
                  </button>
                  
                  {answer && (
                    <div className="mt-6 p-6 bg-gradient-to-r from-purple-50 to-indigo-50 border border-purple-200 rounded-xl">
                      <h3 className="font-semibold text-purple-900 mb-3 flex items-center">
                        <Bot className="h-4 w-4 mr-2" />
                        AI Answer:
                      </h3>
                      <p className="text-purple-800 whitespace-pre-wrap leading-relaxed">{answer}</p>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Tag Filters */}
            {allTags.length > 0 && (
              <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-8">
                <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                  <Tag className="h-5 w-5 mr-2 text-indigo-600" />
                  Filter by Tags
                </h3>
                <div className="flex flex-wrap gap-3">
                  {allTags.map(tag => (
                    <button
                      key={tag}
                      onClick={() => handleTagFilter(tag)}
                      className={`inline-flex items-center px-4 py-2 rounded-full text-sm font-medium transition-all duration-200 ${
                        selectedTags.includes(tag)
                          ? 'bg-indigo-100 text-indigo-800 border-2 border-indigo-300 shadow-sm'
                          : 'bg-gray-100 text-gray-700 border-2 border-transparent hover:bg-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <Tag className="h-3 w-3 mr-2" />
                      {tag}
                    </button>
                  ))}
                </div>
                {selectedTags.length > 0 && (
                  <button
                    onClick={() => setSelectedTags([])}
                    className="mt-4 text-sm text-indigo-600 hover:text-indigo-500 font-medium"
                  >
                    Clear all filters
                  </button>
                )}
              </div>
            )}

            {/* Documents Section */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-xl font-bold text-gray-800 mb-6">Your Documents</h2>
              
              {/* Documents Grid */}
              <div className="grid gap-6 md:grid-cols-2">
                {filteredDocuments.map((doc) => (
                  <div key={doc._id} className="group bg-white rounded-xl border-2 border-gray-100 hover:border-indigo-200 hover:shadow-lg transition-all duration-300 overflow-hidden">
                    <div className="p-6">
                      <div className="flex items-start justify-between mb-4">
                        <div className="flex-1">
                          <Link to={`/documents/${doc._id}`}>
                            <h3 className="text-lg font-semibold text-gray-900 hover:text-indigo-600 cursor-pointer line-clamp-2 group-hover:text-indigo-600 transition-colors duration-200">
                              {doc.title}
                            </h3>
                          </Link>
                          
                          {doc.summary && (
                            <p className="mt-3 text-sm text-gray-600 line-clamp-3 leading-relaxed">
                              {doc.summary}
                            </p>
                          )}
                          
                          {doc.tags.length > 0 && (
                            <div className="mt-4 flex flex-wrap gap-2">
                              {doc.tags.slice(0, 3).map(tag => (
                                <span
                                  key={tag}
                                  className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-700 hover:bg-indigo-100 hover:text-indigo-700 transition-colors duration-200"
                                >
                                  {tag}
                                </span>
                              ))}
                              {doc.tags.length > 3 && (
                                <span className="text-xs text-gray-500 px-2 py-1">+{doc.tags.length - 3} more</span>
                              )}
                            </div>
                          )}
                        </div>
                      </div>
                      
                      <div className="flex items-center text-sm text-gray-500 mb-6 space-x-4">
                        <div className="flex items-center">
                          <div className="h-6 w-6 bg-gray-200 rounded-full flex items-center justify-center mr-2">
                            <User className="h-3 w-3 text-gray-600" />
                          </div>
                          <span className="font-medium">{doc.createdBy.name}</span>
                        </div>
                        <div className="flex items-center">
                          <Clock className="h-4 w-4 mr-1" />
                          <span>{new Date(doc.updatedAt).toLocaleDateString()}</span>
                        </div>
                      </div>

                      {/* AI Action Buttons - only show if user can edit */}
                      {canEdit(doc) && (
                        <div className="mb-4 flex flex-wrap gap-2">
                          <button
                            onClick={() => handleGenerateSummary(doc._id)}
                            disabled={aiLoading[`summary-${doc._id}`]}
                            className="inline-flex items-center px-4 py-2 border border-transparent text-xs font-medium rounded-lg text-indigo-700 bg-indigo-50 hover:bg-indigo-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 transition-all duration-200"
                          >
                            {aiLoading[`summary-${doc._id}`] ? (
                              <>
                                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-indigo-600 mr-2"></div>
                                Generating...
                              </>
                            ) : (
                              <>
                                <Bot className="h-3 w-3 mr-2" />
                                Summarize with Gemini
                              </>
                            )}
                          </button>
                          
                          <button
                            onClick={() => handleGenerateTags(doc._id)}
                            disabled={aiLoading[`tags-${doc._id}`]}
                            className="inline-flex items-center px-4 py-2 border border-transparent text-xs font-medium rounded-lg text-purple-700 bg-purple-50 hover:bg-purple-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 transition-all duration-200"
                          >
                            {aiLoading[`tags-${doc._id}`] ? (
                              <>
                                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-purple-600 mr-2"></div>
                                Generating...
                              </>
                            ) : (
                              <>
                                <Sparkles className="h-3 w-3 mr-2" />
                                Generate Tags
                              </>
                            )}
                          </button>
                        </div>
                      )}
                      
                      {/* Regular Action Buttons */}
                      <div className="flex flex-wrap gap-2">
                        <Link
                          to={`/documents/${doc._id}`}
                          className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50 hover:border-indigo-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200 shadow-sm"
                        >
                          <FileText className="h-4 w-4 mr-2" />
                          View
                        </Link>
                        
                        {canEdit(doc) && (
                          <>
                            <Link
                              to={`/documents/${doc._id}/edit`}
                              className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50 hover:border-indigo-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200 shadow-sm"
                            >
                              <Edit className="h-4 w-4 mr-2" />
                              Edit
                            </Link>
                            
                            <button
                              onClick={() => handleDelete(doc._id)}
                              className="inline-flex items-center px-4 py-2 border border-red-200 text-sm font-medium rounded-lg text-red-700 bg-red-50 hover:bg-red-100 hover:border-red-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 transition-all duration-200 shadow-sm"
                            >
                              <Trash2 className="h-4 w-4 mr-2" />
                              Delete
                            </button>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {filteredDocuments.length === 0 && (
                <div className="text-center py-16">
                  <div className="bg-gray-100 rounded-full h-20 w-20 flex items-center justify-center mx-auto mb-4">
                    <FileText className="h-10 w-10 text-gray-400" />
                  </div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">
                    {selectedTags.length > 0 ? 'No documents match the selected tags' : 'No documents yet'}
                  </h3>
                  <p className="text-gray-500 mb-6 max-w-sm mx-auto">
                    {selectedTags.length > 0 
                      ? 'Try removing some tag filters or create a new document.'
                      : 'Get started by creating your first document and building your knowledge base.'
                    }
                  </p>
                  {selectedTags.length === 0 && (
                    <Link
                      to="/documents/new"
                      className="inline-flex items-center px-6 py-3 border border-transparent shadow-sm text-sm font-medium rounded-lg text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200"
                    >
                      <Plus className="h-5 w-5 mr-2" />
                      Create your first document
                    </Link>
                  )}
                </div>
              )}
            </div>
          </div>

          {/* Enhanced Sidebar */}
          <div className="lg:col-span-1 space-y-6">
            {/* Team Activity Feed */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-lg font-bold text-gray-800 mb-6 flex items-center">
                <TrendingUp className="h-5 w-5 mr-2 text-indigo-600" />
                Team Activity
              </h2>
              
              {recentActivity.length > 0 ? (
                <div className="space-y-4">
                  {recentActivity.slice(0, 5).map((doc) => (
                    <div key={doc._id} className="flex items-start space-x-3 p-3 rounded-lg hover:bg-gray-50 transition-colors duration-200">
                      <div className="flex-shrink-0">
                        <div className="h-10 w-10 bg-indigo-100 rounded-full flex items-center justify-center">
                          <FileText className="h-4 w-4 text-indigo-600" />
                        </div>
                      </div>
                      <div className="flex-1 min-w-0">
                        <Link
                          to={`/documents/${doc._id}`}
                          className="text-sm font-medium text-gray-900 hover:text-indigo-600 block truncate transition-colors duration-200"
                        >
                          {doc.title}
                        </Link>
                        <p className="text-xs text-gray-500 mt-1">
                          Updated by {doc.createdBy.name}
                        </p>
                        <p className="text-xs text-gray-400 mt-1 flex items-center">
                          <Calendar className="h-3 w-3 mr-1" />
                          {new Date(doc.updatedAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <div className="bg-gray-100 rounded-full h-12 w-12 flex items-center justify-center mx-auto mb-3">
                    <TrendingUp className="h-6 w-6 text-gray-400" />
                  </div>
                  <p className="text-sm text-gray-500">No recent activity</p>
                </div>
              )}
            </div>

            {/* Quick Stats */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-lg font-bold text-gray-800 mb-6 flex items-center">
                <Users className="h-5 w-5 mr-2 text-indigo-600" />
                Quick Stats
              </h2>
              <div className="space-y-4">
                <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                  <span className="text-sm font-medium text-gray-600">Total Documents</span>
                  <span className="text-lg font-bold text-gray-900">{documents.length}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                  <span className="text-sm font-medium text-gray-600">Your Documents</span>
                  <span className="text-lg font-bold text-indigo-600">{userDocuments.length}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                  <span className="text-sm font-medium text-gray-600">Total Tags</span>
                  <span className="text-lg font-bold text-gray-900">{allTags.length}</span>
                </div>
              </div>
            </div>

            {/* Popular Tags */}
            {allTags.length > 0 && (
              <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
                <h3 className="text-lg font-bold text-gray-800 mb-4">Popular Tags</h3>
                <div className="flex flex-wrap gap-2">
                  {allTags.slice(0, 6).map(tag => (
                    <span
                      key={tag}
                      className="bg-indigo-100 text-indigo-700 text-sm font-medium px-3 py-1.5 rounded-full cursor-pointer hover:bg-indigo-200 transition-colors duration-200"
                      onClick={() => handleTagFilter(tag)}
                    >
                      {tag}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
