// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import toast from 'react-hot-toast';
import { 
  Plus, 
  FileText, 
  Edit, 
  Trash2, 
  Clock, 
  User,
  Tag,
  Bot,
  MessageCircle
} from 'lucide-react';

const Dashboard = () => {
  const { user } = useAuth();
  const [documents, setDocuments] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTags, setSelectedTags] = useState([]);
  const [allTags, setAllTags] = useState([]);
  const [showQA, setShowQA] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [qaLoading, setQaLoading] = useState(false);

  useEffect(() => {
    fetchDocuments();
    fetchRecentActivity();
  }, []);

  useEffect(() => {
    // Extract all unique tags
    const tags = new Set();
    documents.forEach(doc => {
      doc.tags.forEach(tag => tags.add(tag));
    });
    setAllTags([...tags]);
  }, [documents]);

  const fetchDocuments = async () => {
    try {
      const response = await axios.get('/documents');
      setDocuments(response.data.documents);
    } catch (error) {
      toast.error('Failed to fetch documents');
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentActivity = async () => {
    try {
      const response = await axios.get('/documents/activity');
      setRecentActivity(response.data);
    } catch (error) {
      console.error('Failed to fetch recent activity');
    }
  };

  const handleDelete = async (docId) => {
    if (!window.confirm('Are you sure you want to delete this document?')) {
      return;
    }

    try {
      await axios.delete(`/documents/${docId}`);
      toast.success('Document deleted successfully');
      fetchDocuments();
      fetchRecentActivity();
    } catch (error) {
      toast.error('Failed to delete document');
    }
  };

  const handleTagFilter = (tag) => {
    if (selectedTags.includes(tag)) {
      setSelectedTags(selectedTags.filter(t => t !== tag));
    } else {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  const filteredDocuments = selectedTags.length > 0
    ? documents.filter(doc => 
        selectedTags.some(tag => doc.tags.includes(tag))
      )
    : documents;

  const handleAskQuestion = async () => {
    if (!question.trim()) return;

    setQaLoading(true);
    try {
      const response = await axios.post('/ai/question', { question });
      setAnswer(response.data.answer);
    } catch (error) {
      toast.error('Failed to get answer');
    } finally {
      setQaLoading(false);
    }
  };

  const canEdit = (doc) => {
    return user.role === 'admin' || doc.createdBy._id === user.id;
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          <p className="mt-2 text-gray-600">Manage your team's knowledge base</p>
        </div>
        
        <div className="flex space-x-4">
          <button
            onClick={() => setShowQA(!showQA)}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-purple-600 hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 transition-colors"
          >
            <MessageCircle className="h-4 w-4 mr-2" />
            Team Q&A
          </button>
          
          <Link
            to="/documents/new"
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors"
          >
            <Plus className="h-4 w-4 mr-2" />
            New Document
          </Link>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
        {/* Main Content */}
        <div className="lg:col-span-3">
          {/* Q&A Section */}
          {showQA && (
            <div className="bg-white rounded-lg shadow-sm border p-6 mb-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Bot className="h-5 w-5 mr-2 text-purple-600" />
                Ask AI About Your Documents
              </h2>
              
              <div className="space-y-4">
                <div>
                  <textarea
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Ask a question about your team's documents..."
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500"
                    rows="3"
                  />
                </div>
                
                <button
                  onClick={handleAskQuestion}
                  disabled={qaLoading || !question.trim()}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-purple-600 hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  {qaLoading ? 'Thinking...' : 'Ask AI'}
                </button>
                
                {answer && (
                  <div className="mt-4 p-4 bg-purple-50 border border-purple-200 rounded-md">
                    <h3 className="font-medium text-purple-900 mb-2">AI Answer:</h3>
                    <p className="text-purple-800 whitespace-pre-wrap">{answer}</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Tag Filters */}
          {allTags.length > 0 && (
            <div className="bg-white rounded-lg shadow-sm border p-4 mb-6">
              <h3 className="text-sm font-medium text-gray-900 mb-3">Filter by Tags:</h3>
              <div className="flex flex-wrap gap-2">
                {allTags.map(tag => (
                  <button
                    key={tag}
                    onClick={() => handleTagFilter(tag)}
                    className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                      selectedTags.includes(tag)
                        ? 'bg-indigo-100 text-indigo-800 border border-indigo-200'
                        : 'bg-gray-100 text-gray-800 border border-gray-200 hover:bg-gray-200'
                    }`}
                  >
                    <Tag className="h-3 w-3 mr-1" />
                    {tag}
                  </button>
                ))}
              </div>
              {selectedTags.length > 0 && (
                <button
                  onClick={() => setSelectedTags([])}
                  className="mt-2 text-sm text-indigo-600 hover:text-indigo-500"
                >
                  Clear filters
                </button>
              )}
            </div>
          )}

          {/* Documents Grid */}
          <div className="grid gap-6 md:grid-cols-2 xl:grid-cols-3">
            {filteredDocuments.map((doc) => (
              <div key={doc._id} className="bg-white rounded-lg shadow-sm border hover:shadow-md transition-shadow">
                <div className="p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <Link to={`/documents/${doc._id}`}>
                        <h3 className="text-lg font-semibold text-gray-900 hover:text-indigo-600 cursor-pointer line-clamp-2">
                          {doc.title}
                        </h3>
                      </Link>
                      
                      {doc.summary && (
                        <p className="mt-2 text-sm text-gray-600 line-clamp-3">
                          {doc.summary}
                        </p>
                      )}
                      
                      {doc.tags.length > 0 && (
                        <div className="mt-3 flex flex-wrap gap-1">
                          {doc.tags.slice(0, 3).map(tag => (
                            <span
                              key={tag}
                              className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800"
                            >
                              {tag}
                            </span>
                          ))}
                          {doc.tags.length > 3 && (
                            <span className="text-xs text-gray-500">+{doc.tags.length - 3} more</span>
                          )}
                        </div>
                      )}
                    </div>
                  </div>
                  
                  <div className="mt-4 flex items-center text-sm text-gray-500">
                    <User className="h-4 w-4 mr-1" />
                    <span>{doc.createdBy.name}</span>
                    <Clock className="h-4 w-4 ml-4 mr-1" />
                    <span>{new Date(doc.updatedAt).toLocaleDateString()}</span>
                  </div>
                  
                  <div className="mt-4 flex space-x-2">
                    <Link
                      to={`/documents/${doc._id}`}
                      className="inline-flex items-center px-3 py-1 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors"
                    >
                      <FileText className="h-3 w-3 mr-1" />
                      View
                    </Link>
                    
                    {canEdit(doc) && (
                      <>
                        <Link
                          to={`/documents/${doc._id}/edit`}
                          className="inline-flex items-center px-3 py-1 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors"
                        >
                          <Edit className="h-3 w-3 mr-1" />
                          Edit
                        </Link>
                        
                        <button
                          onClick={() => handleDelete(doc._id)}
                          className="inline-flex items-center px-3 py-1 border border-red-300 text-sm font-medium rounded-md text-red-700 bg-white hover:bg-red-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 transition-colors"
                        >
                          <Trash2 className="h-3 w-3 mr-1" />
                          Delete
                        </button>
                      </>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>

          {filteredDocuments.length === 0 && (
            <div className="text-center py-12">
              <FileText className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">
                {selectedTags.length > 0 ? 'No documents match the selected tags' : 'No documents yet'}
              </h3>
              <p className="mt-1 text-sm text-gray-500">
                {selectedTags.length > 0 
                  ? 'Try removing some tag filters or create a new document.'
                  : 'Get started by creating your first document.'
                }
              </p>
              {selectedTags.length === 0 && (
                <div className="mt-6">
                  <Link
                    to="/documents/new"
                    className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors"
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Create your first document
                  </Link>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Sidebar */}
        <div className="lg:col-span-1">
          <div className="bg-white rounded-lg shadow-sm border p-6">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Team Activity Feed</h2>
            
            {recentActivity.length > 0 ? (
              <div className="space-y-4">
                {recentActivity.map((doc) => (
                  <div key={doc._id} className="flex items-start space-x-3">
                    <div className="flex-shrink-0">
                      <div className="h-8 w-8 bg-indigo-100 rounded-full flex items-center justify-center">
                        <FileText className="h-4 w-4 text-indigo-600" />
                      </div>
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm text-gray-900 truncate">
                        <Link
                          to={`/documents/${doc._id}`}
                          className="font-medium hover:text-indigo-600"
                        >
                          {doc.title}
                        </Link>
                      </p>
                      <p className="text-xs text-gray-500">
                        Updated by {doc.createdBy.name}
                      </p>
                      <p className="text-xs text-gray-400">
                        {new Date(doc.updatedAt).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-sm text-gray-500">No recent activity</p>
            )}
          </div>

          {/* Quick Stats */}
          <div className="mt-6 bg-white rounded-lg shadow-sm border p-6">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Stats</h2>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-sm text-gray-600">Total Documents</span>
                <span className="text-sm font-medium text-gray-900">{documents.length}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm text-gray-600">Your Documents</span>
                <span className="text-sm font-medium text-gray-900">
                  {documents.filter(doc => doc.createdBy._id === user.id).length}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm text-gray-600">Total Tags</span>
                <span className="text-sm font-medium text-gray-900">{allTags.length}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
