// Enhanced Dashboard.js with AI buttons on document cards
import React, { useState, useEffect } from 'react';
import { 
  Plus, 
  FileText, 
  Edit, 
  Trash2, 
  Clock, 
  User,
  Tag,
  Bot,
  MessageCircle,
  Sparkles,
  TrendingUp,
  Activity,
  Search,
  Filter,
  BarChart3
} from 'lucide-react';

const Dashboard = () => {
  // Mock user and data for demonstration
  const user = { id: '1', name: 'Alex Johnson', role: 'admin' };
  
  const [documents, setDocuments] = useState([
    {
      _id: '1',
      title: 'AI Integration Guidelines for Modern Applications',
      summary: 'Comprehensive guide covering best practices for integrating artificial intelligence into web applications, including API usage, performance optimization, and user experience considerations.',
      content: 'Sample content about AI integration...',
      tags: ['AI', 'Integration', 'Best Practices', 'API'],
      createdBy: { _id: '1', name: 'Alex Johnson' },
      updatedAt: new Date().toISOString()
    },
    {
      _id: '2', 
      title: 'Team Collaboration Strategies',
      summary: 'Effective methods for remote team collaboration using modern tools and methodologies.',
      content: 'Sample content about team collaboration...',
      tags: ['Collaboration', 'Remote Work', 'Productivity'],
      createdBy: { _id: '2', name: 'Sarah Chen' },
      updatedAt: new Date(Date.now() - 86400000).toISOString()
    },
    {
      _id: '3',
      title: 'Advanced React Patterns',
      summary: 'Deep dive into advanced React patterns including custom hooks, context optimization, and performance techniques.',
      content: 'Sample content about React patterns...',
      tags: ['React', 'JavaScript', 'Frontend', 'Performance'],
      createdBy: { _id: '1', name: 'Alex Johnson' },
      updatedAt: new Date(Date.now() - 172800000).toISOString()
    }
  ]);
  
  const [recentActivity, setRecentActivity] = useState([
    {
      _id: '1',
      title: 'AI Integration Guidelines',
      createdBy: { name: 'Alex Johnson' },
      updatedAt: new Date().toISOString()
    },
    {
      _id: '2',
      title: 'Team Collaboration Strategies', 
      createdBy: { name: 'Sarah Chen' },
      updatedAt: new Date(Date.now() - 86400000).toISOString()
    }
  ]);
  
  const [loading, setLoading] = useState(false);
  const [selectedTags, setSelectedTags] = useState([]);
  const [allTags, setAllTags] = useState(['AI', 'Integration', 'Best Practices', 'API', 'Collaboration', 'Remote Work', 'Productivity', 'React', 'JavaScript', 'Frontend', 'Performance']);
  const [showQA, setShowQA] = useState(false);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [qaLoading, setQaLoading] = useState(false);
  const [aiLoading, setAiLoading] = useState({});

  useEffect(() => {
    fetchDocuments();
    fetchRecentActivity();
  }, []);

  useEffect(() => {
    // Extract all unique tags
    const tags = new Set();
    documents.forEach(doc => {
      doc.tags.forEach(tag => tags.add(tag));
    });
    setAllTags([...tags]);
  }, [documents]);

  const fetchDocuments = async () => {
    try {
      // Mock API call
      setDocuments(documents);
    } catch (error) {
      console.error('Failed to fetch documents');
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentActivity = async () => {
    try {
      // Mock API call
      setRecentActivity(recentActivity);
    } catch (error) {
      console.error('Failed to fetch recent activity');
    }
  };

  const handleDelete = async (docId) => {
    if (!window.confirm('Are you sure you want to delete this document?')) {
      return;
    }

    try {
      // Mock delete
      setDocuments(documents.filter(doc => doc._id !== docId));
      console.log('Document deleted successfully');
    } catch (error) {
      console.error('Failed to delete document');
    }
  };

  const handleTagFilter = (tag) => {
    if (selectedTags.includes(tag)) {
      setSelectedTags(selectedTags.filter(t => t !== tag));
    } else {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  const filteredDocuments = selectedTags.length > 0
    ? documents.filter(doc => 
        selectedTags.some(tag => doc.tags.includes(tag))
      )
    : documents;

  const handleAskQuestion = async () => {
    if (!question.trim()) return;

    setQaLoading(true);
    try {
      // Mock AI response
      setTimeout(() => {
        setAnswer('Based on your team\'s documents, here are the key insights about AI integration: Focus on API optimization, user experience design, and performance monitoring. Consider implementing gradual rollout strategies for new AI features.');
        setQaLoading(false);
      }, 2000);
    } catch (error) {
      console.error('Failed to get answer');
    }
  };

  // Generate summary for existing document
  const handleGenerateSummary = async (docId) => {
    const doc = documents.find(d => d._id === docId);
    if (!doc) return;

    setAiLoading(prev => ({ ...prev, [`summary-${docId}`]: true }));
    try {
      // Mock AI summary generation
      setTimeout(() => {
        console.log('Summary generated and saved!');
        setAiLoading(prev => ({ ...prev, [`summary-${docId}`]: false }));
      }, 2000);
    } catch (error) {
      console.error('Failed to generate summary');
    }
  };

  // Generate tags for existing document
  const handleGenerateTags = async (docId) => {
    const doc = documents.find(d => d._id === docId);
    if (!doc) return;

    setAiLoading(prev => ({ ...prev, [`tags-${docId}`]: true }));
    try {
      // Mock tag generation
      setTimeout(() => {
        console.log('Tags generated and saved!');
        setAiLoading(prev => ({ ...prev, [`tags-${docId}`]: false }));
      }, 2000);
    } catch (error) {
      console.error('Failed to generate tags');
    }
  };

  const canEdit = (doc) => {
    return user.role === 'admin' || doc.createdBy._id === user.id;
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
        <div className="relative">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          <div className="absolute inset-0 flex items-center justify-center">
            <Bot className="h-8 w-8 text-indigo-600 animate-pulse" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      {/* Background Pattern */}
      <div className="absolute inset-0 bg-grid-slate-100/[0.04] bg-[size:75px_75px] [mask-image:linear-gradient(0deg,white,rgba(255,255,255,0.6))]" />
      
      <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header Section */}
        <div className="flex justify-between items-center mb-8">
          <div className="relative">
            <h1 className="text-4xl font-bold bg-gradient-to-r from-slate-900 via-indigo-900 to-slate-900 bg-clip-text text-transparent">
              AI-Powered Dashboard
            </h1>
            <p className="mt-2 text-slate-600 font-medium">Manage your team's knowledge base with intelligent assistance</p>
            <div className="absolute -left-2 -top-2 w-20 h-20 bg-indigo-500/10 rounded-full blur-xl animate-pulse" />
          </div>
          
          <div className="flex space-x-4">
            <button
              onClick={() => setShowQA(!showQA)}
              className="group relative inline-flex items-center px-6 py-3 border border-transparent text-sm font-medium rounded-xl text-white bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-500 hover:to-indigo-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 transition-all duration-300 transform hover:scale-105 shadow-lg shadow-purple-500/25"
            >
              <MessageCircle className="h-4 w-4 mr-2 group-hover:scale-110 transition-transform duration-300" />
              AI Q&A Assistant
              <div className="absolute -inset-1 bg-gradient-to-r from-purple-600 to-indigo-600 rounded-xl blur opacity-30 group-hover:opacity-50 transition duration-300" />
            </button>
            
            <a
              href="/documents/new"
              className="group relative inline-flex items-center px-6 py-3 border border-transparent text-sm font-medium rounded-xl text-white bg-gradient-to-r from-indigo-600 to-blue-600 hover:from-indigo-500 hover:to-blue-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-300 transform hover:scale-105 shadow-lg shadow-indigo-500/25"
            >
              <Plus className="h-4 w-4 mr-2 group-hover:rotate-90 transition-transform duration-300" />
              Create Document
              <div className="absolute -inset-1 bg-gradient-to-r from-indigo-600 to-blue-600 rounded-xl blur opacity-30 group-hover:opacity-50 transition duration-300" />
            </a>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-3">
            {/* Q&A Section */}
            {showQA && (
              <div className="relative bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-8 mb-8 overflow-hidden">
                <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-br from-purple-400/20 to-indigo-400/20 rounded-full blur-2xl" />
                <h2 className="text-xl font-bold text-slate-900 mb-6 flex items-center">
                  <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-indigo-500 rounded-xl flex items-center justify-center mr-3 shadow-lg">
                    <Bot className="h-5 w-5 text-white" />
                  </div>
                  Ask AI About Your Documents
                </h2>
                
                <div className="space-y-6">
                  <div className="relative">
                    <textarea
                      value={question}
                      onChange={(e) => setQuestion(e.target.value)}
                      placeholder="Ask a question about your team's documents..."
                      className="w-full px-4 py-4 border-2 border-slate-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500 bg-white/50 backdrop-blur-sm transition-all duration-300 resize-none"
                      rows="4"
                    />
                  </div>
                  
                  <button
                    onClick={handleAskQuestion}
                    disabled={qaLoading || !question.trim()}
                    className="relative inline-flex items-center px-6 py-3 border border-transparent text-sm font-medium rounded-xl text-white bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-500 hover:to-indigo-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 transform hover:scale-105 shadow-lg"
                  >
                    <Sparkles className="h-4 w-4 mr-2 animate-spin" style={{ animationDuration: qaLoading ? '1s' : '0s' }} />
                    {qaLoading ? 'AI is thinking...' : 'Ask AI Assistant'}
                  </button>
                  
                  {answer && (
                    <div className="mt-6 p-6 bg-gradient-to-r from-purple-50 to-indigo-50 border-2 border-purple-200/50 rounded-2xl backdrop-blur-sm">
                      <h3 className="font-bold text-purple-900 mb-3 flex items-center">
                        <Bot className="h-5 w-5 mr-2" />
                        AI Response:
                      </h3>
                      <p className="text-purple-800 whitespace-pre-wrap leading-relaxed">{answer}</p>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Tag Filters */}
            {allTags.length > 0 && (
              <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-6 mb-8">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-bold text-slate-900 flex items-center">
                    <Filter className="h-5 w-5 mr-2 text-indigo-600" />
                    Filter by Tags
                  </h3>
                  {selectedTags.length > 0 && (
                    <button
                      onClick={() => setSelectedTags([])}
                      className="text-sm text-indigo-600 hover:text-indigo-500 font-medium transition-colors duration-200"
                    >
                      Clear all filters
                    </button>
                  )}
                </div>
                <div className="flex flex-wrap gap-3">
                  {allTags.map(tag => (
                    <button
                      key={tag}
                      onClick={() => handleTagFilter(tag)}
                      className={`group inline-flex items-center px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 transform hover:scale-105 ${
                        selectedTags.includes(tag)
                          ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg shadow-indigo-500/25'
                          : 'bg-slate-100 text-slate-700 border-2 border-slate-200 hover:bg-slate-200 hover:border-slate-300'
                      }`}
                    >
                      <Tag className={`h-3 w-3 mr-2 transition-transform duration-300 ${
                        selectedTags.includes(tag) ? 'scale-110' : 'group-hover:scale-110'
                      }`} />
                      {tag}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Documents Grid */}
            <div className="grid gap-8 md:grid-cols-1 xl:grid-cols-2">
              {filteredDocuments.map((doc) => (
                <div key={doc._id} className="group relative bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 hover:shadow-2xl transition-all duration-500 transform hover:scale-[1.02] overflow-hidden">
                  {/* Card Background Gradient */}
                  <div className="absolute inset-0 bg-gradient-to-br from-indigo-50/50 via-white/50 to-purple-50/50 opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
                  
                  <div className="relative p-8">
                    <div className="flex items-start justify-between mb-6">
                      <div className="flex-1">
                        <a href={`/documents/${doc._id}`}>
                          <h3 className="text-xl font-bold text-slate-900 hover:text-indigo-600 cursor-pointer line-clamp-2 group-hover:text-indigo-600 transition-colors duration-300">
                            {doc.title}
                          </h3>
                        </a>
                        
                        {doc.summary && (
                          <p className="mt-3 text-slate-600 line-clamp-3 leading-relaxed">
                            {doc.summary}
                          </p>
                        )}
                        
                        {doc.tags.length > 0 && (
                          <div className="mt-4 flex flex-wrap gap-2">
                            {doc.tags.slice(0, 3).map(tag => (
                              <span
                                key={tag}
                                className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-gradient-to-r from-slate-100 to-slate-200 text-slate-700 border border-slate-200"
                              >
                                {tag}
                              </span>
                            ))}
                            {doc.tags.length > 3 && (
                              <span className="text-xs text-slate-500 flex items-center px-2">
                                +{doc.tags.length - 3} more
                              </span>
                            )}
                          </div>
                        )}
                      </div>
                    </div>
                    
                    <div className="flex items-center text-sm text-slate-500 mb-6">
                      <div className="w-8 h-8 bg-gradient-to-r from-indigo-400 to-purple-400 rounded-full flex items-center justify-center mr-3">
                        <User className="h-4 w-4 text-white" />
                      </div>
                      <span className="font-medium">{doc.createdBy.name}</span>
                      <div className="mx-3 w-1 h-1 bg-slate-300 rounded-full" />
                      <Clock className="h-4 w-4 mr-1" />
                      <span>{new Date(doc.updatedAt).toLocaleDateString()}</span>
                    </div>

                    {/* AI Action Buttons */}
                    <div className="mb-6 flex flex-wrap gap-3">
                      <button
                        onClick={() => handleGenerateSummary(doc._id)}
                        disabled={aiLoading[`summary-${doc._id}`]}
                        className="group inline-flex items-center px-4 py-2 border-2 border-indigo-200 text-sm font-medium rounded-xl text-indigo-700 bg-indigo-50 hover:bg-indigo-100 hover:border-indigo-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 transition-all duration-300 transform hover:scale-105"
                      >
                        <Bot className={`h-4 w-4 mr-2 ${aiLoading[`summary-${doc._id}`] ? 'animate-spin' : 'group-hover:scale-110'} transition-transform duration-300`} />
                        {aiLoading[`summary-${doc._id}`] ? 'Generating...' : 'AI Summarize'}
                      </button>
                      
                      <button
                        onClick={() => handleGenerateTags(doc._id)}
                        disabled={aiLoading[`tags-${doc._id}`]}
                        className="group inline-flex items-center px-4 py-2 border-2 border-purple-200 text-sm font-medium rounded-xl text-purple-700 bg-purple-50 hover:bg-purple-100 hover:border-purple-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 transition-all duration-300 transform hover:scale-105"
                      >
                        <Sparkles className={`h-4 w-4 mr-2 ${aiLoading[`tags-${doc._id}`] ? 'animate-spin' : 'group-hover:scale-110'} transition-transform duration-300`} />
                        {aiLoading[`tags-${doc._id}`] ? 'Generating...' : 'Smart Tags'}
                      </button>
                    </div>
                    
                    {/* Regular Action Buttons */}
                    <div className="flex space-x-3">
                      <a
                        href={`/documents/${doc._id}`}
                        className="group inline-flex items-center px-4 py-2 border-2 border-slate-200 text-sm font-medium rounded-xl text-slate-700 bg-white hover:bg-slate-50 hover:border-slate-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-slate-500 transition-all duration-300 transform hover:scale-105"
                      >
                        <FileText className="h-4 w-4 mr-2 group-hover:scale-110 transition-transform duration-300" />
                        View
                      </a>
                      
                      {canEdit(doc) && (
                        <>
                          <a
                            href={`/documents/${doc._id}/edit`}
                            className="group inline-flex items-center px-4 py-2 border-2 border-slate-200 text-sm font-medium rounded-xl text-slate-700 bg-white hover:bg-slate-50 hover:border-slate-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-slate-500 transition-all duration-300 transform hover:scale-105"
                          >
                            <Edit className="h-4 w-4 mr-2 group-hover:scale-110 transition-transform duration-300" />
                            Edit
                          </a>
                          
                          <button
                            onClick={() => handleDelete(doc._id)}
                            className="group inline-flex items-center px-4 py-2 border-2 border-red-200 text-sm font-medium rounded-xl text-red-700 bg-red-50 hover:bg-red-100 hover:border-red-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 transition-all duration-300 transform hover:scale-105"
                          >
                            <Trash2 className="h-4 w-4 mr-2 group-hover:scale-110 transition-transform duration-300" />
                            Delete
                          </button>
                        </>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {filteredDocuments.length === 0 && (
              <div className="text-center py-16 bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20">
                <div className="w-24 h-24 mx-auto mb-6 bg-gradient-to-r from-indigo-100 to-purple-100 rounded-full flex items-center justify-center">
                  <FileText className="h-12 w-12 text-indigo-400" />
                </div>
                <h3 className="text-xl font-bold text-slate-900 mb-2">
                  {selectedTags.length > 0 ? 'No documents match the selected tags' : 'No documents yet'}
                </h3>
                <p className="text-slate-600 mb-6 max-w-md mx-auto">
                  {selectedTags.length > 0 
                    ? 'Try removing some tag filters or create a new document to get started.'
                    : 'Get started by creating your first document and let AI help organize your knowledge.'
                  }
                </p>
                {selectedTags.length === 0 && (
                  <a
                    href="/documents/new"
                    className="inline-flex items-center px-6 py-3 border border-transparent shadow-lg text-base font-medium rounded-xl text-white bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-500 hover:to-purple-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-300 transform hover:scale-105"
                  >
                    <Plus className="h-5 w-5 mr-2" />
                    Create your first document
                  </a>
                )}
              </div>
            )}
          </div>

          {/* Enhanced Sidebar */}
          <div className="lg:col-span-1 space-y-8">
            {/* Activity Feed */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-6">
              <h2 className="text-lg font-bold text-slate-900 mb-6 flex items-center">
                <div className="w-8 h-8 bg-gradient-to-r from-green-400 to-blue-400 rounded-lg flex items-center justify-center mr-3">
                  <Activity className="h-4 w-4 text-white" />
                </div>
                Team Activity
              </h2>
              
              {recentActivity.length > 0 ? (
                <div className="space-y-4">
                  {recentActivity.map((doc) => (
                    <div key={doc._id} className="group flex items-start space-x-4 p-3 rounded-xl hover:bg-slate-50 transition-colors duration-200">
                      <div className="flex-shrink-0">
                        <div className="h-10 w-10 bg-gradient-to-r from-indigo-400 to-purple-400 rounded-full flex items-center justify-center shadow-lg">
                          <FileText className="h-4 w-4 text-white" />
                        </div>
                      </div>
                      <div className="flex-1 min-w-0">
                        <a
                          href={`/documents/${doc._id}`}
                          className="text-sm font-semibold text-slate-900 hover:text-indigo-600 transition-colors duration-200 block truncate"
                        >
                          {doc.title}
                        </a>
                        <p className="text-xs text-slate-500 mt-1">
                          Updated by {doc.createdBy.name}
                        </p>
                        <p className="text-xs text-slate-400">
                          {new Date(doc.updatedAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <div className="w-12 h-12 mx-auto mb-3 bg-slate-100 rounded-full flex items-center justify-center">
                    <Activity className="h-6 w-6 text-slate-400" />
                  </div>
                  <p className="text-sm text-slate-500">No recent activity</p>
                </div>
              )}
            </div>

            {/* Enhanced Quick Stats */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 p-6">
              <h2 className="text-lg font-bold text-slate-900 mb-6 flex items-center">
                <div className="w-8 h-8 bg-gradient-to-r from-orange-400 to-red-400 rounded-lg flex items-center justify-center mr-3">
                  <BarChart3 className="h-4 w-4 text-white" />
                </div>
                Quick Stats
              </h2>
              <div className="space-y-4">
                <div className="flex justify-between items-center p-3 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-xl border border-indigo-100">
                  <span className="text-sm font-medium text-slate-700">Total Documents</span>
                  <span className="text-lg font-bold text-indigo-600">{documents.length}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-gradient-to-r from-green-50 to-blue-50 rounded-xl border border-green-100">
                  <span className="text-sm font-medium text-slate-700">Your Documents</span>
                  <span className="text-lg font-bold text-green-600">
                    {documents.filter(doc => doc.createdBy._id === user.id).length}
                  </span>
                </div>
                <div className="flex justify-between items-center p-3 bg-gradient-to-r from-purple-50 to-pink-50 rounded-xl border border-purple-100">
                  <span className="text-sm font-medium text-slate-700">Total Tags</span>
                  <span className="text-lg font-bold text-purple-600">{allTags.length}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
